SET AREA_CODE = #{areaCode}, USER_ID = #{userId}, USER_NAME = #{userName}, NICK_NAME = #{nickName}, PHONE_PREFIX = #{phonePrefix}, USER_PHONE = #{userPhone}, UPDATE_TIME = #{updateTime}, USER_TYPE = #{userType}, EMAIL = #{email}, GENDER = #{gender}, BRANCH_CODE = #{branchCode}, VERIFIED_METHOD = #{verifiedMethod}, ACTIVE_FLAG = #{activeFlag}, CREATE_BY = #{createBy}, CREATE_TIME = #{createTime}, VERSION = #{version}, DEL_FLAG = #{delFlag}, BECOME_A_MEMBER = #{becomeAMember}, JUMP_BOOKING = #{jumpBooking}, REMARKS = #{remarks}
WHERE ID = #{id}
